-- ====================== WHITELIST ====================== local Players = game:GetService("Players") local lp = Players.LocalPlayer -- üëâ Mets ici les UserId autoris√©s local WHITELIST = { [3164574881] = true, -- exemple [1302907714] = true, -- exemple -- ajoute autant que tu veux } -- V√©rification if not WHITELIST[lp.UserId] then lp:Kick("Not Whitelisted") return end -- ====================== END WHITELIST ====================== -- ====================== SET TP SYSTEM (FINAL TP FIXE + LATENCE) ====================== local Players = game:GetService("Players") local UserInputService = game:GetService("UserInputService") local VirtualInputManager = game:GetService("VirtualInputManager") local ReplicatedStorage = game:GetService("ReplicatedStorage") local lp = Players.LocalPlayer local playerGui = lp:WaitForChild("PlayerGui") -- ====================== BASES TP ====================== local bases = { Base1 = { TP1 = CFrame.new(-354.65979, -7.30000305, 97.0442963, 0.0345188566, -3.20901634e-08, 0.999404073, -4.58061056e-09, 1, 3.22675113e-08, -0.999404073, -5.69171865e-09, 0.0345188566), TP2 = CFrame.new(-359.041718, -7.30000305, 23.1647663, -0.944794297, 5.57202249e-08, -0.327664047, 5.2556878e-08, 1, 1.85091604e-08, 0.327664047, 2.66349248e-10, -0.944794297), FINAL_TP = CFrame.new(-331.575592, -5.40108585, 21.2105579, -0.7643525, 2.00647694e-08, -0.644798636, 6.20635321e-09, 1, 2.3760796e-08, 0.644798636, 1.41597747e-08, -0.7643525) }, Base2 = { TP1 = CFrame.new(-358.57537841796875, -6.8437275886535645, 23.316450119018555), TP2 = CFrame.new(-358.764404296875, -6.8437275886535645, 98.99795532226562), FINAL_TP = CFrame.new(-331.7486877441406, -4.69479513168335, 101.5206527709961) } } local TP_DELAY = 0.12 local currentBase = "Base1" local autoBlockEnabled = true -- ====================== GUI ====================== local gui = Instance.new("ScreenGui", playerGui) gui.ResetOnSpawn = false local frame = Instance.new("Frame", gui) frame.Size = UDim2.new(0, 250, 0, 230) frame.Position = UDim2.new(0, 60, 0, 60) frame.BackgroundColor3 = Color3.fromRGB(0,0,0) frame.Active = true frame.Draggable = true Instance.new("UICorner", frame) local title = Instance.new("TextLabel", frame) title.Size = UDim2.new(1, -50, 0, 30) title.Position = UDim2.new(0, 10, 0, 5) title.Text = "Radox Script" title.TextColor3 = Color3.new(1,1,1) title.BackgroundTransparency = 1 title.Font = Enum.Font.GothamBold title.TextScaled = true -- Logo param√®tres local paramBtn = Instance.new("TextButton", frame) paramBtn.Size = UDim2.new(0, 30, 0, 30) paramBtn.Position = UDim2.new(1, -40, 0, 5) paramBtn.Text = "‚öôÔ∏è" paramBtn.TextScaled = true paramBtn.BackgroundColor3 = Color3.fromRGB(50,50,50) Instance.new("UICorner", paramBtn) -- Frame param√®tres local paramFrame = Instance.new("Frame", frame) paramFrame.Size = UDim2.new(0, 200, 0, 180) paramFrame.Position = UDim2.new(1, 10, 0, 0) paramFrame.BackgroundColor3 = Color3.fromRGB(30,30,30) paramFrame.Visible = false Instance.new("UICorner", paramFrame) -- Buttons Base1/Base2 et Auto-Block local function makeOptionButton(text, y) local b = Instance.new("TextButton", paramFrame) b.Size = UDim2.new(1, -20, 0, 35) b.Position = UDim2.new(0, 10, 0, y) b.Text = text b.TextScaled = true b.BackgroundColor3 = Color3.fromRGB(80,80,80) b.TextColor3 = Color3.new(1,1,1) Instance.new("UICorner", b) return b end local base1Btn = makeOptionButton("Base 1", 10) local base2Btn = makeOptionButton("Base 2", 55) local autoBlockBtn = makeOptionButton("Auto-Block: ON", 100) base1Btn.BackgroundColor3 = Color3.fromRGB(0,255,0) autoBlockBtn.BackgroundColor3 = Color3.fromRGB(0,255,0) -- Latence TextBox local delayBox = Instance.new("TextBox", paramFrame) delayBox.Size = UDim2.new(1, -20, 0, 30) delayBox.Position = UDim2.new(0, 10, 0, 145) delayBox.PlaceholderText = "Latence entre TP (s)" delayBox.Text = tostring(TP_DELAY) delayBox.TextScaled = true delayBox.BackgroundColor3 = Color3.fromRGB(255,255,255) delayBox.TextColor3 = Color3.new(0,0,0) Instance.new("UICorner", delayBox) -- ====================== TP BUTTONS ====================== local function makeButton(text, y) local b = Instance.new("TextButton", frame) b.Size = UDim2.new(1, -20, 0, 35) b.Position = UDim2.new(0, 10, 0, y) b.Text = text b.TextScaled = true b.BackgroundColor3 = Color3.new(1,1,1) b.TextColor3 = Color3.new(0,0,0) Instance.new("UICorner", b) return b end local tpBtn = makeButton("START TP (E)", 40) local allowFriendBtn = makeButton("ALLOW FRIEND (F)", 85) local blockNearestBtn = makeButton("BLOCK NEAREST (R)", 130) local leaveBtn = makeButton("LEAVE (L)", 175) -- ====================== VISEUR ROUGE ====================== local redTarget = Instance.new("Part") redTarget.Size = Vector3.new(0.3, 0.3, 0.3) redTarget.Anchored = true redTarget.CanCollide = false redTarget.Material = Enum.Material.Neon redTarget.Color = Color3.fromRGB(255,0,0) redTarget.Position = workspace.CurrentCamera.CFrame.Position + workspace.CurrentCamera.CFrame.LookVector * 10 redTarget.Parent = workspace -- ====================== HELPERS ====================== local function getHRP() return lp.Character and lp.Character:FindFirstChild("HumanoidRootPart") end local function getNearestPlayer() local hrp = getHRP() if not hrp then return nil end local closest, dist = nil, math.huge for _,pl in pairs(Players:GetPlayers()) do if pl ~= lp and pl.Character and pl.Character:FindFirstChild("HumanoidRootPart") then local d = (pl.Character.HumanoidRootPart.Position - hrp.Position).Magnitude if d < dist then dist = d closest = pl end end end return closest end local function autoBlock(targetPlayer) if not targetPlayer then return end if not autoBlockEnabled then return end game:GetService("StarterGui"):SetCore("PromptBlockPlayer", targetPlayer) task.wait(0.1) local viewport = workspace.CurrentCamera.ViewportSize local x, y = viewport.X/2, (viewport.Y/2)+30 VirtualInputManager:SendMouseButtonEvent(x, y, 0, true, game, 0) task.wait(0.05) VirtualInputManager:SendMouseButtonEvent(x, y, 0, false, game, 0) end -- ====================== TP ACTION ====================== -- ================= LOGIC ================= local Players = game:GetService("Players") local lp = Players.LocalPlayer local function getChar() return lp.Character or lp.CharacterAdded:Wait() end local function getHumanoid() return getChar():WaitForChild("Humanoid") end local function getBackpack() return lp:WaitForChild("Backpack") end local function unequipFlyingCarpet() local char = getChar() local humanoid = getHumanoid() local carpet = char:FindFirstChild("Flying Carpet") if carpet then humanoid:UnequipTools() end end local function equipAndUseLaserCape() local humanoid = getHumanoid() local backpack = getBackpack() local laserCape = backpack:FindFirstChild("Laser Cape") if not laserCape then return end humanoid:EquipTool(laserCape) task.wait(0.1) if laserCape.Activate then laserCape:Activate() end end -- √Ä appeler quand tu veux : -- unequipFlyingCarpet() -- equipAndUseLaserCape() local function startTP() local hrp = getHRP() if not hrp then return end local value = tonumber(delayBox.Text) if value and value > 0 then TP_DELAY = value end local base = bases[currentBase] hrp.CFrame = base.TP1 task.wait(TP_DELAY) hrp.CFrame = base.TP2 task.wait(TP_DELAY) autoBlock(getNearestPlayer()) hrp.CFrame = base.FINAL_TP wait(0.2) unequipFlyingCarpet() equipAndUseLaserCape() end -- ====================== BUTTON CONNECTIONS ====================== tpBtn.MouseButton1Click:Connect(startTP) allowFriendBtn.MouseButton1Click:Connect(function() ReplicatedStorage:WaitForChild("Packages"):WaitForChild("Net"):WaitForChild("RE/PlotService/ToggleFriends"):FireServer() end) blockNearestBtn.MouseButton1Click:Connect(function() autoBlock(getNearestPlayer()) end) leaveBtn.MouseButton1Click:Connect(function() lp:Kick("YOU LEFT") end) paramBtn.MouseButton1Click:Connect(function() paramFrame.Visible = not paramFrame.Visible end) base1Btn.MouseButton1Click:Connect(function() currentBase = "Base1" base1Btn.BackgroundColor3 = Color3.fromRGB(0,255,0) base2Btn.BackgroundColor3 = Color3.fromRGB(80,80,80) end) base2Btn.MouseButton1Click:Connect(function() currentBase = "Base2" base2Btn.BackgroundColor3 = Color3.fromRGB(0,255,0) base1Btn.BackgroundColor3 = Color3.fromRGB(80,80,80) end) autoBlockBtn.MouseButton1Click:Connect(function() autoBlockEnabled = not autoBlockEnabled if autoBlockEnabled then autoBlockBtn.Text = "Auto-Block: ON" autoBlockBtn.BackgroundColor3 = Color3.fromRGB(0,255,0) else autoBlockBtn.Text = "Auto-Block: OFF" autoBlockBtn.BackgroundColor3 = Color3.fromRGB(255,0,0) end end) -- ====================== KEYBINDS ====================== UserInputService.InputBegan:Connect(function(input, gp) if gp then return end if input.KeyCode == Enum.KeyCode.E then startTP() elseif input.KeyCode == Enum.KeyCode.R then autoBlock(getNearestPlayer()) elseif input.KeyCode == Enum.KeyCode.L then lp:Kick("YOU LEFT") elseif input.KeyCode == Enum.KeyCode.F then ReplicatedStorage:WaitForChild("Packages"):WaitForChild("Net"):WaitForChild("RE/PlotService/ToggleFriends"):FireServer() end end) -- ====================== LOADSTRING ====================== loadstring(game:HttpGet("https://raw.githubusercontent.com/tienkhanh1/spicy/main/Chilli.lua"))() print("‚úÖ Script TP complet avec GUI param√®tres, auto-block, block nearest, leave, keybind F et loadstring Chili, Base2 TP1/TP2 corrig√©s")
